import React, { useEffect, useMemo, useRef, useState } from "react";
import { createPortal } from "react-dom";

export default function App({ plugin }) {
  const pluginRootRef = useRef(null);
  const [log, setLog] = useState([]);
  const [pluginActions, setPluginActions] = useState([]);
  const [pluginState, setPluginState] = useState({});
  const [focusTarget, setFocusTarget] = useState(null);

  // Tiny event bus the plugin can use.
  const bus = useMemo(() => {
    const listeners = new Map(); // eventName -> Set<fn>
    return {
      subscribe(event, fn) {
        if (!listeners.has(event)) listeners.set(event, new Set());
        listeners.get(event).add(fn);
        return () => listeners.get(event)?.delete(fn);
      },
      publish(event, payload) {
        listeners.get(event)?.forEach((fn) => fn(payload));
      },
    };
  }, []);

  // Provide a constrained host API to the plugin.
  const hostApi = useMemo(
    () => ({
      subscribe: bus.subscribe,
      publish: (event, payload) => {
        setLog((l) => [...l, { event, payload, ts: Date.now() }]);
        bus.publish(event, payload);

        // Generic state tracking for any plugin
        if (event.startsWith('state:')) {
          setPluginState(prev => ({
            ...prev,
            [event.replace('state:', '')]: payload
          }));
        }
      },
      getHostInfo: () => ({ version: "1.0.0", name: "DemoHost" }),
      setActions: (actions = []) => setPluginActions(actions),
      requestFocus: (target) => setFocusTarget(target),
    }),
    [bus]
  );

  // Handle global keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e) => {
      if ((e.metaKey || e.ctrlKey) && e.key === 'j') {
        e.preventDefault();
        hostApi.publish('host:focus-request', { source: 'keyboard-shortcut' });
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  // Mount/unmount the plugin strictly within its container.
  useEffect(() => {
    if (!plugin || typeof plugin.mount !== "function") return;
    const rootEl = pluginRootRef.current;
    const cleanup = plugin.mount(rootEl, hostApi);

    // Let the host know what actions the plugin exposes (optional)
    if (Array.isArray(plugin.actions)) setPluginActions(plugin.actions);

    return () => {
      try {
        plugin.unmount?.(rootEl); // plugin cleans up ONLY inside its root
      } finally {
        // clear UI space
        if (rootEl) rootEl.innerHTML = "";
        setPluginActions([]);
        setPluginState({});
      }
    };
  }, [plugin, hostApi]);

  // Handle focus requests
  useEffect(() => {
    if (focusTarget && pluginRootRef.current) {
      const element = pluginRootRef.current.querySelector(`[data-focus-id="${focusTarget}"]`);
      if (element && element.focus) {
        element.focus();
        setFocusTarget(null);
      }
    }
  }, [focusTarget]);

