```jsx
import React, { useEffect } from 'react';
import { createRoot } from 'react-dom/client';
import App from './app.jsx';
import Plugin from './plugin.jsx';

// Bootstrap the React app
const container = document.getElementById('root');
const root = createRoot(container);

// Add global keyboard shortcut handler
function useGlobalShortcut(plugin, hostApi) {
  useEffect(() => {
    if (!plugin || !hostApi) return;

    const handleKeyDown = (e) => {
      // Check for Cmd+J or Ctrl+J
      if ((e.metaKey || e.ctrlKey) && e.key === 'j') {
        e.preventDefault();
        // Generic focus request - plugin should document its focus selector
        hostApi.publish('host:focus-request', { selector: '[data-focus="note-input"]' });
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [plugin, hostApi]);
}

// Create a wrapper component to handle the shortcut
function AppWithShortcut() {
  const hostApiRef = React.useRef();

  // Expose hostApi to the shortcut handler
  const setHostApi = (api) => {
    hostApiRef.current = api;
  };

  useGlobalShortcut(Plugin, hostApiRef.current);

  return <App plugin={Plugin} ref={setHostApi} />;
}

// Render the app
root.render(
  <React.StrictMode>
    <AppWithShortcut />
  </React.StrictMode>
);
